import React, { useState }  from 'react'

import { useSelector } from 'react-redux';

import { ThemeProvider } from '@mui/material/styles';
import { CssBaseline, StyledEngineProvider } from '@mui/material';
import Joyride, {  STATUS } from 'react-joyride';

// routing
import Routes from 'routes';

// defaultTheme
import themes from 'themes';

// project imports
import NavigationScroll from 'layout/NavigationScroll';
import { steps } from "./views/utilities/Steps";

// ==============================|| APP ||============================== //

const App = () => {
    const [notDone, setnotDone] = useState(true)

    //window.localStorage.setItem('notDone', true)
    if(localStorage.getItem('notDone') == 'false'){
        localStorage.setItem('notDone','false');
    }
    else {localStorage.setItem('notDone', 'true')}

    const customization = useSelector((state) => state.customization);

    return (
        <StyledEngineProvider injectFirst>
            <ThemeProvider theme={themes(customization)}>
                <CssBaseline />
                <NavigationScroll>
                    <Joyride
                        continuous={true}  
                        //disableOverlay        

                        callback={(data) => {
                            const { status } = data;

                            if ([STATUS.FINISHED].includes(status) || [STATUS.SKIPPED].includes(status)) {
                                localStorage.setItem('notDone', 'false');
                                setnotDone(false);
                            } 

                        }} 
                        scrollToFirstStep={true} //el botoncito
                        showProgress={true}
                        showSkipButton={true}
                        //run = {localStorage.getItem('notDone') == 'true' ? (true):(false)}
                        steps={steps}
                        styles={{
                            options: {
                            zIndex: 10000,
                            },
                            buttonClose: {
                                display: 'none',
                            },
                        }}
                    />
                    <Routes />
                </NavigationScroll>
            </ThemeProvider>
        </StyledEngineProvider>
    );
};

export default App;
