import ReactDOM from 'react-dom';

// third party
import { BrowserRouter } from 'react-router-dom';
import { Provider } from 'react-redux';

// project imports
import * as serviceWorker from 'serviceWorker';
import App from 'App';
import { store } from 'store';
import Joyride, {  STATUS } from 'react-joyride';
import { steps } from "./views/utilities/Steps";
// style + assets
import 'assets/scss/style.scss';

// ==============================|| REACT DOM RENDER  ||============================== //

ReactDOM.render(
    <Provider store={store}>
        <BrowserRouter>
        <Joyride
                                            continuous={true}  
                                            //disableOverlay        

                                            callback={(data) => {
                                                const { status } = data;

                                                if ([STATUS.FINISHED].includes(status) || [STATUS.SKIPPED].includes(status)) {
                                                    localStorage.setItem('notDone', 'false');
                                                    setnotDone(false);
                                                } 

                                            }} 
                                            scrollToFirstStep={true} //el botoncito
                                            showProgress={true}
                                            showSkipButton={true}
                                            run = {localStorage.getItem('notDone') == 'true' ? (true):(false)}
                                            steps={steps}
                                            styles={{
                                                options: {
                                                zIndex: 10000,
                                                },
                                                buttonClose: {
                                                    display: 'none',
                                                },
                                            }}
                                        />
            <App />
        </BrowserRouter>
    </Provider>,
    document.getElementById('root')
);


serviceWorker.unregister();
